from flask import Flask, redirect, request, abort, render_template, url_for, flashfrom flask_security import Security, SQLAlchemyUserDatastore, UserMixin, RoleMixin, login_required, url_for_securityfrom flask_sqlalchemy import SQLAlchemyimport datetimeimport configapp = Flask(__name__)app.config['SQLALCHEMY_DATABASE_URI'] = config.dbLocationapp.config['SECRET_KEY'] = config.secretKeyapp.config['SECURITY_PASSWORD_HASH'] = "pbkdf2_sha512"app.config['SECURITY_PASSWORD_SALT'] = config.passwordSaltapp.config['SECURITY_REGISTERABLE'] = Trueapp.config['SECURITY_RECOVERABLE'] = Trueapp.config['SECURITY_CHANGABLE'] = Truedb = SQLAlchemy(app)class Stream(db.Model):    __tablename__="Stream"    id = db.Column(db.Integer, primary_key=True)    streamKey = db.Column(db.String)    streamUser = db.Column(db.String)    def __init__(self, streamKey, streamUser):        self.streamKey = streamKey        self.streamUser = streamUser    def __repr__(self):        return '<id %r>' % self.idroles_users = db.Table('roles_users',        db.Column('user_id', db.Integer(), db.ForeignKey('user.id')),        db.Column('role_id', db.Integer(), db.ForeignKey('role.id')))class Role(db.Model, RoleMixin):    id = db.Column(db.Integer(), primary_key=True)    name = db.Column(db.String(80), unique=True)    description = db.Column(db.String(255))class User(db.Model, UserMixin):    id = db.Column(db.Integer, primary_key=True)    email = db.Column(db.String(255), unique=True)    password = db.Column(db.String(255))    active = db.Column(db.Boolean())    confirmed_at = db.Column(db.DateTime())    roles = db.relationship('Role', secondary=roles_users,                            backref=db.backref('users', lazy='dynamic'))# Setup Flask-Securityuser_datastore = SQLAlchemyUserDatastore(db, User, Role)security = Security(app, user_datastore)db.create_all()def usernameToKey(userName):    try:        key = next(key for key, value in config.authKey.items() if value == userName)    except:        return "*Unknown User*"    return key@app.route('/')def main_page():    activeStreams = Stream.query.all()    return render_template('index.html',streamList=activeStreams)@app.route('/view/<user>/')def view_page(user):    streamData = Stream.query.filter_by(streamUser=user).first()    streamURL = 'http://' + config.ipAddress + '/live/' + user + '/index.m3u8'    return render_template('player.html', stream=streamData, streamURL=streamURL)@app.route('/auth-key', methods=['POST'])def streamkey_check():    key = request.form['name']    ipaddress = request.form['addr']    if config.authKey.has_key(key):        returnMessage = {'time': str(datetime.datetime.now()), 'status': 'Successful Key Auth', 'key':str(key), 'user': str(config.authKey[key]), 'ipAddress': str(ipaddress)}        print(returnMessage)        newStream = Stream(key,str(config.authKey[key]))        db.session.add(newStream)        db.session.commit()        return redirect('rtmp://' + config.ipAddress + '/stream-data/' + config.authKey[key], code=302)    else:        returnMessage = {'time': str(datetime.datetime.now()), 'status': 'Failed Key Auth', 'key':str(key), 'ipAddress': str(ipaddress)}        print(returnMessage)        return abort(400)@app.route('/auth-user', methods=['POST'])def user_auth_check():    key = request.form['name']    ipaddress = request.form['addr']    streamKey = usernameToKey(key)    authedStream = Stream.query.filter_by(streamKey=streamKey).first()    if authedStream is not None:        returnMessage = {'time': str(datetime.datetime.now()), 'status': 'Successful User Auth', 'key': str(streamKey), 'user': str(key), 'ipAddress': str(ipaddress)}        print(returnMessage)        return 'OK'    else:        returnMessage = {'time': str(datetime.datetime.now()), 'status': 'Failed User Auth. No Authorized Stream Key', 'user': str(key), 'ipAddress': str(ipaddress)}        print(returnMessage)        return abort(400)@app.route('/deauth-user', methods=['POST'])def user_deauth_check():    key = request.form['name']    ipaddress = request.form['addr']    authedStream = Stream.query.filter_by(streamKey=key).first()    if authedStream is not None:        db.session.delete(authedStream)        db.session.commit()        returnMessage = {'time': str(datetime.datetime.now()), 'status': 'Stream Closed', 'key': str(key), 'user': str(config.authKey[key]), 'ipAddress': str(ipaddress)}        print(returnMessage)        return 'OK'    else:        returnMessage = {'time': str(datetime.datetime.now()), 'status': 'Stream Closure Failure - No Such Stream', 'key': str(key), 'ipAddress': str(ipaddress)}        print(returnMessage)        return abort(400)if __name__ == '__main__':    app.jinja_env.auto_reload = True    app.config['TEMPLATES_AUTO_RELOAD'] = True    app.run(debug=False, port=5000)